import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "../db.js";
import Button from "react-bootstrap/Button";
import { Redirect } from "react-router-dom";

export default class Email extends React.Component {
  state = {
    toname: "",
    toemail: "",
    subject: "",
    message: ""
  };

  handleCreateDB = async () => {
    if (
      await DB.Emails.SendEmail({
        toname: this.state.toname,
        toemail: this.state.toemail,
        subject: this.state.subject,
        message: this.state.message
      })
    ) {
      console.log("Email has been sent");
    }
  };

  handleToName = event => {
    this.setState({ toname: event.target.value });
  };
  handleToEmail = event => {
    this.setState({ toemail: event.target.value });
  };
  handleSubject = event => {
    this.setState({ subject: event.target.value });
  };
  handleMessage = event => {
    this.setState({ message: event.target.value });
  };

  render() {
    return (
      <div>
        <h2>Emails Section</h2>
        <hr />
        <dl>
          <dt>To name</dt>
          <dd>
            <input type="text" onChange={this.handleToName} />
          </dd>
        </dl>
        <dl>
          <dt>To Eamil</dt>
          <dd>
            <input type="text" onChange={this.handleToEmail} />
          </dd>
        </dl>
        <dl>
          <dt>Subject</dt>
          <dd>
            <input type="text" onChange={this.handleSubject} />
          </dd>
        </dl>
        <dl>
          <dt>Message</dt>
          <dd>
            <input type="text" onChange={this.handleMessage} />
          </dd>
        </dl>
        <p>
          <Button onClick={this.handleCreateDB}>Send</Button>
        </p>
      </div>
    );
  }
}
