import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "db.js";
import auth from "auth.js"
import Button from "react-bootstrap/Button";
import { Redirect } from "react-router-dom";

export default class Advertisment extends React.Component {
  state = {
    Name: "",
    Phone: "",
    types: "",
    StartDateTime: "",
    EndDateTime: "",
    MediaContent: "",
    Description: "",
    Status: "",
    User: null,
    isCreated: false
  };
  componentDidMount = async () =>{
    if(auth.isLoggedIn()){
      const json = await db.Users.findByQuery("profile");
      console.log("Advertisment/User: ",json)
      this.setState({
        User : json
      })
    }

  }
  render(){
    return this.state.isCreated ? (
      <Redirect to="/profile" />
    ) : (
        this.state.User ? 
        <div>
          <h2>Advertisment</h2>
          <br />

          <label>Email: </label>
          <input
            value={this.state.User.Email}
            type="text"
            disabled
          ></input>
          {"   "}
          <label>Name: </label>
          <input
            value={this.state.Name}
            type="text"
            disabled
          ></input>
          <br></br>

          <label>Start Time: </label>
          <input
            value={this.state.Name}
            type="text"
            disabled
          ></input>
          <br></br>

          <label>End Time: </label>
          <input
            value={this.state.Name}
            type="text"
            disabled
          ></input>
          <br></br>


          <label>Role: </label>
          <input
            value={this.state.User.Role.Name}
            type="text"
            disabled
          ></input>
          <br></br>

          <Button onClick={() => this.handleEditDB()}>Save</Button>
        </div>
        :
        <h1>Loading...</h1>
      );
  }
}
