import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "../db.js";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link, Redirect } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Auth from "../auth";

export default class RentingCreate extends React.Component {
  state = {
    Description: "",
    Capacity: "",
    isCreated: false,
    store: null,
    Date: "",
    Times: [
      "09:00:00",
      "10:00:00",
      "11:00:00",
      "13:00:00",
      "14:00:00",
      "15:00:00"
    ],
    Time: null
  };

  async componentDidMount() {
    const store = await DB.Stores.findOne(this.props.match.params.id);
    console.log(store);
    this.setState({ store });
  }

  handleDate = event => {
    //const movie = await DB.assets.findOne(this.props.match.params.id);
    //Customer has selected date
    //now display all time slots on that date
    //with availble/unavailable made obvious

    this.setState({ Date: event.target.value });
  };

  handleTime = (event, Time) => {
    this.setState({ Time });
  };

  nextHour = time => {
    return 1 * time.split(":")[0] + 1 + ":00";
  };

  handleBook = async () => {
    console.log(this.state.Date);
    console.log(this.state.Time);
    console.log(this.state.movie.Id);

    if (
      await DB.assetBookings.create({
        AssetId: this.state.movie.Id,
        StartDateTime: `${this.state.Date}T${this.state.Time}`,
        EndDateTime: `${this.state.Date}T${this.nextHour(this.state.Time)}`,
        TotalPrice: 0,
        Status: ""
      })
    ) {
      this.setState({ isCreated: true });
    }
  };

  isAvailable = time => {
    // search for time in all times in
    //this.state.movie.assetBookings.Bookings;
    const search = `${this.state.Date}T${time}`;
    return (
      this.state.movie.AssetBookings.filter(
        assetBooking => assetBooking.Booking.StartDateTime === search
      ).length === 0
    );
  };

  handleDescription = event => {
    this.setState({ Description: event.target.value });
  };
  handleCapacity = event => {
    this.setState({ Capacity: event.target.value });
  };

  render() {
    return this.state.isCreated ? (
      <Redirect to="/Customers/profile/" />
    ) : (
      <div>
        <h2>Create Movie Show</h2>

        <label>Description: </label>
        <dd>
          {this.state.movie &&
            `${this.state.movie.Description} (${this.state.movie.PricingModel.Amount})`}
        </dd>
        <br></br>
        <label>date: </label>
        <input
          type="date"
          onChange={this.handleDate}
          value={this.state.Date}
        ></input>
        <br></br>
        <label>Capacity: </label>
        <input type="text" onChange={this.handleCapacity}></input>
        <br></br>
        <Button onClick={this.handleBook}>Create</Button>
        <br></br>
        {this.state.Date != "" &&
          this.state.Times.map(time => (
            <li key={time}>
              {time}
              {this.state.movie && this.isAvailable(time) && (
                <Form.Check
                  inline
                  type="radio"
                  name="time"
                  onChange={event => this.handleTime(event, time)}
                  value={this.state.Time === time}
                ></Form.Check>
              )}
            </li>
          ))}
      </div>
    );
  }
}
