import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "./db"
import auth from "./auth"
import Button from "react-bootstrap/Button";
import { Redirect } from "react-router-dom";
import Form from "react-bootstrap/Form"

export default class CreateAdvertisment extends React.Component {
  state = {
    Advertisment:{
      UserEmail: "",
      Types: "",
      StartDateTime: "",
      EndDateTime: "",
      MediaContent: "",
      Description: "",
      Status: "",
    },
    User: null,
    isCreated: false
  };
  componentDidMount = async () =>{
    if(auth.isLoggedIn()){
      const json = await DB.Users.findByQuery("profile");
      const temp = this.state.Advertisment;
      console.log("Advertisment/User: ",json);
      temp.UserEmail = json.Email;
      this.setState({
        User : json,
        Advertisment : temp
      })
    }

  }
  HandleCreate = async () =>{
  }
  handleType = (evemt) => {
    const temp = this.state.Advertisment;
    temp.Types = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleStartDateTime = (evemt) => {
    const temp = this.state.Advertisment;
    temp.StartDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleEndDateTime = (evemt) => {
    const temp = this.state.Advertisment;
    temp.EndDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleMediaContent = (evemt) => {
    const temp = this.state.Advertisment;
    temp.MediaContent = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleDescription = (evemt) => {
    const temp = this.state.Advertisment;
    temp.Description = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleStatus = (evemt) => {
    const temp = this.state.Advertisment;
    temp.Status = event.target.value;
    this.setState({Advertisment : temp})
  } 

  render(){
    return this.state.isCreated ? (
      <Redirect to="/profile" />
    ) : (
        this.state.User ? 


    <div>
               <h2>Advertisment</h2>
          <br />

          <label>Email: </label>
          <input
            value={this.state.User.Email}
            type="text"
            disabled
          ></input>
          {"   "}
          <label>Name: </label>
          <input
            value={this.state.User.Name}
            type="text"
            disabled
          ></input>
          <br></br>

          <label>Start Time: </label>
          <input
            onChange={() => this.handleStartDateTime}
            type="text"
          ></input>
          <br></br>

          <label>End Time: </label>
          <input
          onChange={() => this.handleEndDateTime}
            type="text"
          ></input>
          <br></br>

          <label>Attached Media</label>
          <input
          onChange={() => this.handleMediaContent}
            type="text"
          ></input>
          <br></br>

          <label>Status</label>
          <input
          onChange={() => this.handleStatus}
            type="text"
          ></input>
          <br></br>
          <Button onClick={() => this.handleEditDB()}>Save</Button>
        </div>
        :
        <h1>Loading...</h1>
      );
  }
}
