import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "./db"
import auth from "./auth"
import Table from "react-bootstrap/Table"
import {Button} from "react-bootstrap";
import { Redirect } from "react-router-dom";
import {TextField,MenuItem,Select,InputLabel,FormHelperText,Grid} from '@material-ui/core';

export default class CreateAdvertisment extends React.Component {
  state = {
    Advertisment:{
      UserEmail: "",
      Types: "",
      StartDateTime: "",
      EndDateTime: "",
      MediaContent: "",
      Description: "",
      Status: "",
    },
    User: null,
    isCreated: false
  };
  componentDidMount = async () =>{
    if(auth.isLoggedIn()){
      const json = await DB.Users.findByQuery("profile");
      const temp = this.state.Advertisment;
      console.log("Advertisment/User: ",json);
      temp.UserEmail = json.Email;
      this.setState({
        User : json,
        Advertisment : temp
      })
    }

  }
  HandleCreate = async () =>{
    console.log("Finaly Object: ",this.state.Advertisment)
  }
  handleType = event => {
    console.log("type ", event.target.value)
    const temp = this.state.Advertisment
    temp.Types = event.target.value
    this.setState({Advertisment : temp})
  }
  handleStartDateTime = event => {
    const temp = this.state.Advertisment;
    temp.StartDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleEndDateTime = event => {
    const temp = this.state.Advertisment;
    temp.EndDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleMediaContent = event => {
    const temp = this.state.Advertisment;
    temp.MediaContent = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleDescription = event => {
    const temp = this.state.Advertisment;
    temp.Description = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleStatus = event => {
    const temp = this.state.Advertisment;
    temp.Status = event.target.value;
    this.setState({Advertisment : temp})
  } 

  render(){
    return this.state.isCreated ? (
      <Redirect to="/profile" />
    ) : (
        this.state.User ? 


        <Grid container spacing={1} style={{width: 300, marginLeft:"40%",textAlign:"center"}}>
       
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
      
         <h2>Advertisment</h2>
        <Table>
        <tbody>
          <tr>
            <th>
            <TextField
              id="standard-basic"
              value={this.state.User.Email}
              label="Email"
              margin="normal"
              disabled
            />
        <InputLabel id="demo-simple-select-required-label">Type</InputLabel>
        <Select 
          labelId="demo-simple-select-required-label"
          id="demo-simple-select-required"
          onChange={this.handleType}
        >
          <MenuItem value="" disabled>
            Advertisment Type
          </MenuItem>
          <MenuItem value={"10"}>Sport</MenuItem>
          <MenuItem value={"20"}>Music</MenuItem>
          <MenuItem value={"30"}>Food</MenuItem>
        </Select>
           <TextField
              id="datetime-local"
              type="datetime-local"
              label="Start Time"
              defaultValue="2017-05-24T10:30"
              InputLabelProps={{
                shrink: true,
              }}
              onChange={this.handleStartDateTime}
            />
            <TextField
              id="datetime-local"
              type="datetime-local"
              label="End Time"
              defaultValue="2017-05-24T10:30"
              InputLabelProps={{
                shrink: true,
              }}
              onChange={this.handleEndDateTime}
            />
          <TextField
              id="standard-basic"
              onChange={this.handleMediaContent}
              label="Attach Media"
              margin="normal"
            />
          <TextField
          id="outlined-multiline-static"
          label="Description"
          multiline
          rows="4"
          onChange={this.handleDescription}
          margin="normal"
          variant="outlined"
        />
          </th>
          </tr>
        </tbody>
        <tbody>
        </tbody>
          </Table>
          <Button onClick={() => this.HandleCreate()}>Save</Button>
          </Grid>
        :
        <h1>Loading...</h1>
      );
  }
}
