import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "./db"
import auth from "./auth"
import Table from "react-bootstrap/Table"
import {Button,Dropdown} from "react-bootstrap";
import { Redirect } from "react-router-dom";
import {TextField} from '@material-ui/core';

export default class CreateAdvertisment extends React.Component {
  state = {
    Advertisment:{
      UserEmail: "",
      Types: "",
      StartDateTime: "",
      EndDateTime: "",
      MediaContent: "",
      Description: "",
      Status: "",
    },
    User: null,
    isCreated: false
  };
  componentDidMount = async () =>{
    if(auth.isLoggedIn()){
      const json = await DB.Users.findByQuery("profile");
      const temp = this.state.Advertisment;
      console.log("Advertisment/User: ",json);
      temp.UserEmail = json.Email;
      this.setState({
        User : json,
        Advertisment : temp
      })
    }

  }
  HandleCreate = async () =>{
    console.log("Finaly Object: ",this.state.Advertisment)
  }
  handleType = event => {
    console.log("type ", event.target.value)
    const temp = this.state.Advertisment
    temp.Types = event.target.value
    this.setState({Advertisment : temp})
  }
  handleStartDateTime = event => {
    const temp = this.state.Advertisment;
    temp.StartDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleEndDateTime = event => {
    const temp = this.state.Advertisment;
    temp.EndDateTime = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleMediaContent = event => {
    const temp = this.state.Advertisment;
    temp.MediaContent = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleDescription = event => {
    const temp = this.state.Advertisment;
    temp.Description = event.target.value;
    this.setState({Advertisment : temp})
  }
  handleStatus = event => {
    const temp = this.state.Advertisment;
    temp.Status = event.target.value;
    this.setState({Advertisment : temp})
  } 

  render(){
    return this.state.isCreated ? (
      <Redirect to="/profile" />
    ) : (
        this.state.User ? 


    <div>
      
         <h2>Advertisment</h2>
        <Table>
        <tbody>
          <tr>
            <th>
              <label>Email: </label>
            </th>
            <td>
              <input
                value={this.state.User.Email}
                type="text"
                disabled
            ></input>
            </td>
          </tr>
        </tbody>

        <tbody>
          <tr>
            <th>
            <label>Type: </label>
            </th>
            <td>
            <Dropdown>
            <Dropdown.Menu>
                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <input
              onChange={this.handleType}
              type="text"
            ></input>
            </td>
          </tr>
          </tbody>

        
          <tbody>
          <tr>
            <th>
          <label>Start Time: </label>
           </th>
           <td>
           <TextField
              id="datetime-local"
              type="datetime-local"
              defaultValue="2017-05-24T10:30"
              InputLabelProps={{
                shrink: true,
              }}
              onChange={this.handleStartDateTime}
            />
          </td>
          </tr>
          </tbody>

          <tbody>
          <tr>
            <th>
          <label>End Time: </label>
            </th>
            <td>
            <TextField
              id="datetime-local"
              type="datetime-local"
              defaultValue="2017-05-24T10:30"
              InputLabelProps={{
                shrink: true,
              }}
              onChange={this.handleEndDateTime}
            />
            </td>
          </tr>
        </tbody>
        
        <tbody>
          <tr>
            <th>
          <label>Attached Media</label>
          </th>
          <td>
          <input
          onChange={this.handleMediaContent}
            type="text"
          ></input>
            </td>
          </tr>
        </tbody>

        <tbody>
          <tr>
            <th>
          <label>Description</label>
          </th>
          <td>
          <input
          onChange={this.handleDescription}
            type="text"
          ></input>
          </td>
          </tr>
        </tbody>

        <tbody>
          <tr>
            <th>
          <label>Status</label>
          </th>
          <td>
          <input
          onChange={this.handleStatus}
            type="text"
          ></input>
          </td>
          </tr>
        </tbody>
        <tbody>
        <td>
          <Button onClick={() => this.HandleCreate()}>Save</Button>
          </td>
        </tbody>
          </Table>
        </div>
        :
        <h1>Loading...</h1>
      );
  }
}
