import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DB from "../db.js";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link, Redirect } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Auth from "../auth";

export default class RentingCreate extends React.Component {
  state = {
    renter: "",
    securityCompanies: [],
    Description: "",
    Capacity: "",
    isCreated: false,
    store: null,
    Date: "",
    StartDateTime: null,
    EndDateTime: null,
    TotalPrice: 0
  };

  async componentDidMount() {
    const store = await DB.Stores.findOne(this.props.match.params.id);
    const renters = await DB.Renters.findByName(Auth.username());
    const securityCompanies = await DB.Securities.findAll();
    const renter = renters[0];
    console.log(renter);
    //console.log(securityCompanies);
    //console.log(store);
    this.setState({ store });
    this.setState({ securityCompanies });
    this.setState({ renter });
  }

  handleStartDate = event => {
    //const movie = await DB.assets.findOne(this.props.match.params.id);
    //Customer has selected date
    //now display all time slots on that date
    //with availble/unavailable made obvious

    this.setState({ StartDateTime: event.target.value });
  };
  handleEndDate = event => {
    //const movie = await DB.assets.findOne(this.props.match.params.id);
    //Customer has selected date
    //now display all time slots on that date
    //with availble/unavailable made obvious

    this.setState({ EndDateTime: event.target.value });
  };

  handleTime = (event, Time) => {
    this.setState({ Time });
  };

  handleBook = async () => {
    console.log(this.state.Date);
    console.log(this.state.Time);
    console.log(this.state.movie.Id);

    if (
      await DB.assetBookings.create({
        RenterEmail: this.state.renter,
        StartDateTime: `${this.state.StartDateTime}T00:00:00`,
        EndDateTime: `${this.state.EndDateTime}T00:00:00`,
        TotalPrice: 0,
        Status: ""
      })
    ) {
      this.setState({ isCreated: true });
    }
  };

  handleTotalPrice = () => {};

  isAvailable = time => {
    // search for time in all times in
    //this.state.movie.assetBookings.Bookings;
    const search = `${this.state.Date}T${time}`;
    return (
      this.state.movie.AssetBookings.filter(
        assetBooking => assetBooking.Booking.StartDateTime === search
      ).length === 0
    );
  };

  handleDescription = event => {
    this.setState({ Description: event.target.value });
  };
  handleCapacity = event => {
    this.setState({ Capacity: event.target.value });
  };

  render() {
    return this.state.isCreated ? (
      <Redirect to="/profile" />
    ) : (
      <div>
        <h2>Please Fill the Form Below</h2>
        <label>Renter:</label>
        <input value={this.state.renter.Email}></input>
        <br />
        <label>Store Name:</label>
        <input value={this.state.renter.StoreName}></input>
        <br></br>
        <label>Start date: </label>
        <input
          type="date"
          onChange={this.handleStartDate}
          value={this.state.Date}
        ></input>
        <br />
        <label>End date: </label>
        <input
          type="date"
          onChange={this.handleEndDate}
          value={this.state.Date}
        ></input>
        <br></br>
        <label>Total price: </label>
        <input
          type="float"
          value={this.state.TotalPrice}
          onChange={this.handleCapacity}
        ></input>
        <br></br>
        <Button onClick={this.handleBook}>Create</Button>
        <br></br>
        {this.state.securityCompanies.map((company, index) => (
          <li key={time}>
            {time}
            {this.state.movie && this.isAvailable(time) && (
              <Form.Check
                inline
                type="radio"
                name="time"
                onChange={event => this.handleStartTime(event, time)}
                value={this.state.Time === time}
              ></Form.Check>
            )}
          </li>
        ))}
      </div>
    );
  }
}
